<div class="pd-20">
    <form action="" method="post" class="Huiform">
      <table class="table table-border table-bordered table-bg">
        <tbody>
          <tr>
            <th width="100" class="text-r"><span class="c-red">*</span> 频道属性：</th>
            <td><input type="radio"  value="0" name="attr" checked="checked">内部频道<input type="radio"  value="1" name="attr">外部链接</td>
          </tr>
          <tr>
            <th class="text-r"><span class="c-red">*</span> 频道名称：</th>
            <td><input type="text" class="input-text" value="" name="title"></td>
          </tr>
           <tr>
            <th class="text-r"><span class="c-red">*</span> 窗口打开方式：</th>
            <td><input type="radio"  value="_blank" name="target" checked="checked">新窗口<input name="target" type="radio"  value="_self">当前窗口</td>
          </tr>
           <tr>
            <th class="text-r"><span class="c-red">*</span>频道首页规则：</th>
            <td><input type="text" class="input-text" value="0" name="index_rule"></td>
          </tr>
           <tr>
            <th class="text-r"><span class="c-red">*</span>栏目列表规则：</th>
            <td><input type="text" class="input-text" value="" name="list_rule"></td>
          </tr>
           <tr>
            <th class="text-r"><span class="c-red">*</span>内容页面规则：</th>
            <td><input type="text" class="input-text" value="" name="content_rule"></td>
          </tr>
           <tr>
            <th class="text-r"><span class="c-red">*</span>seo标题：</th>
            <td><input type="text" class="input-text" value="" name="seo_title"></td>
          </tr>
           <tr>
            <th class="text-r"><span class="c-red">*</span>seo关键字：</th>
            <td><input type="text" class="input-text" value="" name="seo_keyword"></td>
          </tr>
          <tr>
            <th class="text-r"><span class="c-red">*</span>seo描述：</th>
            <td><input type="text" class="input-text" value="" name="seo_description"></td>
          </tr>
           <tr>
            <th class="text-r"><span class="c-red">*</span>导航栏是否显示：</th>
            <td><input type="radio"  value="1" name="is_show" checked="checked">是<input type="radio"  value="0" name="is_show">否</td>
          </tr>
          <tr>
            <th class="text-r"><span class="c-red">*</span>排序：</th>
            <td><input type="text" class="input-text" value="10" name="sort"></td>
          </tr>
          <tr>
            <th></th>
            <td><button class="btn btn-success radius" type="submit"><i class="icon-ok"></i> 确定</button></td>
          </tr>
        </tbody>
      </table>
    </form>
  </div>